(7651, 'T3_TOUR_GUIDE_29_CONTENT', '<div class=''t3-admin-tour-img''><img src=''http://joomlart.s3.amazonaws.com/images/jat3v3-documents/tour-guide/collapse-menu.png'' alt='''' /></div><p>Enable this option to use default Bootstrap navigation (dropdown menu style) on small screens like iPhone, tablet</p>', '/home/networksoftcom/public_html/administrator/language/en-GB/en-GB.plg_system_t3.ini'),
(7652, 'T3_TOUR_GUIDE_30_TITLE', 'Custom Code', '/home/networksoftcom/public_html/administrator/language/en-GB/en-GB.plg_system_t3.ini'),
